var http = require('http')
function simplehttp(){

}

simplehttp.prototype.create = function(data){
    http.createServer(function(req,res){
        res.writeHead(200,{'Content-Type':'text/html'});
        res.write(data);
        res.end();
    }).listen(3000)
}

module.exports = simplehttp;